new Vue({el:"#app",data:{todos:[],newTodo:""},mounted:function(){this.$http.get("/api/todo").then(function(n){this.todos=n.body})},methods:{addTodo:function(){if(this.newTodo.length!==0){var n={text:this.newTodo,isDone:!1};this.$http.post("/api/todo",n).then(function(){this.todos.push(n);this.newTodo=""},function(){console.log("Noe gikk galt.")})}},toggleDone:function(n){var t={id:n.id,text:n.text,isDone:!n.isDone};this.$http.put("/api/todo/"+n.id,t).then(function(){n.isDone=!n.isDone})}}});